mapper.select:
SELECT
	,
	ROUND(
		6378.138 * 2 * ASIN(
			SQRT(
				POW(
					SIN(
						(
							local_x * PI() / 180 - ${localX} * PI() / 180
						) / 2
					),
					2
				) + COS(local_x * PI() / 180) * COS(${localX} * PI() / 180) * POW(
					SIN(
						(
							local_y * PI() / 180 - ${localY} * PI() / 180
						) / 2
					),
					2
				)
			)
		) * 1000
	) AS distance
FROM
	t_auto_point
	
		
	       and ztd like concat('%',#{ztdName},'%')
	    
	
HAVING
	distance <= 5000
	
ORDER BY
	distance ASC;

SELECT ap.* FROM t_auto_point AS ap INNER JOIN t_user_address AS ua ON ap.id = ua.ztd_id WHERE ua.user_id = #{userId,jdbcType=INTEGER}
and ua.selected=#{selected,jdbcType=VARCHAR},
and ua.address is null and ua.phone is null and ua.name is null order by ua.selected desc,ua.id desc
